<?phpdefined( '_JEXEC' ) or die( 'Restricted access' );jimport( 'joomla.application.component.view');class MdagreementViewSingle extends JView{function display($tpl = null){	$params =& JFactory::getApplication()->getParams();$acceptlink = $params->get('accept_link');	$link = $acceptlink;$this->assign('link', $link);$denylink = $params->get('denied_link');	$dlink = $denylink;$this->assign('dlink', $dlink);$accepttext = $params->get('accept_text_field');$this->assign('accepttext', $accepttext);$deniedtext = $params->get('denied_text_field');$this->assign('deniedtext', $deniedtext);$deniedlinkshow = $params->get('denied_link_show');$this->assign('deniedlinkshow', $deniedlinkshow);$acceptlinkshow = $params->get('accept_link_show');$this->assign('acceptlinkshow', $acceptlinkshow);$maintext = $params->get('main_text');$this->assign('maintext', $maintext);$maintexttitle = $params->get('main_texttitle');$this->assign('maintexttitle', $maintexttitle);	parent::display($tpl);}}